import { INCREMENT, DECREMENT } from "../constant";

const initState = { count: 0 };
export default function countReducer(prevState = initState, action) {
  const { type, data } = action;
  console.log("countReducer", data);
  switch (type) {
    case INCREMENT:
      return prevState + data;
    case DECREMENT:
      return prevState - data;
    default:
      return prevState;
  }
}
