import { ADD_PERSON } from "../constant";

const initState = [];
export default function personReducer(prevState = initState, action) {
  const { type, payload } = action;
  // console.log(type);
  switch (type) {
    case ADD_PERSON:
      return { ...prevState, payload };
    default:
      return prevState;
  }
}
