import { INCREMENT, INCREMENT_ASYNC } from "../constant";
import { put, takeEvery } from "redux-saga/effects";

// export function* increment() {
//   yield takeEvery(increment);
// }

export function* incrementAsync(miliseconds) {
  yield setTimeout(() => ({ type: INCREMENT }), 500);
}

export default function* countSaga() {
  yield takeEvery(INCREMENT_ASYNC, incrementAsync(500));
}
